{
    const svgRaw = htl.svg`<svg></svg>`
    const svg = d3.select(svgRaw)
    .attr("width",500)
    .attr("height",500)
    .style("background-color","#e5e4f0")

    function randomData() {
        return Array.from({length: 50}, () => Math.floor(Math.random() * 100))
    }

    function colourMap(num) {
        if (num >= 0 && num <= 65) { return "yellow"}
        if (num >= 66 && num <= 80) { return "black"}
        if (num>= 81 && num <=85) {return "red"}
        if (num>= 86 && num <=100) {return "grey"}
    }

    function horizontalStreets(yPosArray) {
        for (let i=0; i<yPosArray.length; i++){
            svg.selectAll("horizontal")
            .data(randomData())
            .join(
                enter => enter.append("rect")
                    .attr("class","horizontal")
                    .attr("x", function(d,i) {return i*10})
                    .attr("y", yPosArray[i])
                    .attr("width", 10)
                    .attr("height", 10)
                    .attr("fill", function(d){return colourMap(d)})
                    .on("mouseover", function(d) {
                        d3.select(this)
                            .raise()
                            .transition()
                            .attr("width",25)
                            .attr("height",25)
                            .attr("stroke","black")

                    })
                    .on("mouseout", function(d) {
                        d3.select(this)
                            .transition()
                            .attr("width",10)
                            .attr("height",10)
                            .attr("stroke","")
                    }),
               update => update,
               exit => exit.remove()
            )
        }
    }

    function verticalStreets(xPosArray) {
        for (let i=0; i<xPosArray.length; i++){
            svg.selectAll("vertical")
            .data(randomData())
            .join(
                enter => enter.append("rect")
                    .attr("class","vertical")
                    .attr("x", xPosArray[i])
                    .attr("y", function(d,i) {return i*10})
                    .attr("width", 10)
                    .attr("height", 10)
                    .attr("fill", function(d){return colourMap(d)})
                    .on("mouseover", function(d) {
                        d3.select(this)
                            .raise()
                            .transition()
                            .attr("width",25)
                            .attr("height",25)
                            .attr("stroke","black")

                    })
                    .on("mouseout", function(d) {
                        d3.select(this)
                            .transition()
                            .attr("width",10)
                            .attr("height",10)
                            .attr("stroke","")
                    }),
               update => update,
               exit => exit.remove()
            )

        }
    }

    function moveBlock(d, i) {
        d3.select(this)
        .transition()
            .attr("x", Math.floor(Math.random() * 500))
            .attr("y", Math.floor(Math.random() * 500))
    }

    // Initial draw
    horizontalStreets([10,50,120,150,220,250,280,340,440])
    verticalStreets([10, 30, 70, 140, 300,330,420,440,480,500])

    var blue1 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 50)
        .attr("y", 80)
        .attr("width", 30)
        .attr("height", 30)
        .style("fill", "blue");

    var blue2 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 50)
        .attr("y", 350)
        .attr("width", 40)
        .attr("height", 40)
        .style("fill", "blue");

    var blue3 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 160)
        .attr("y", 260)
        .attr("width", 30)
        .attr("height", 50)
        .style("fill", "blue");

    var blue4 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 380)
        .attr("y", 160)
        .attr("width", 50)
        .attr("height", 100)
        .style("fill", "blue");

    var blue5 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 410)
        .attr("y", 350)
        .attr("width", 40)
        .attr("height", 40)
        .style("fill", "blue");

    var red1 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 80)
        .attr("y", 20)
        .attr("width", 20)
        .attr("height", 90)
        .style("fill", "red");

    var red2 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 130)
        .attr("y", 20)
        .attr("width", 40)
        .attr("height", 60)
        .style("fill", "red");

    var red3 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 80)
        .attr("y", 270)
        .attr("width", 60)
        .attr("height", 40)
        .style("fill", "red");

    var red4 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 290)
        .attr("y", 200)
        .attr("width", 60)
        .attr("height", 50)
        .style("fill", "red");

    var red5 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 340)
        .attr("y", 300)
        .attr("width", 50)
        .attr("height", 70)
        .style("fill", "red");

    var gray1 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 140)
        .attr("y", 40)
        .attr("width", 30)
        .attr("height", 30)
        .style("fill", "gray");

    var gray2 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 230)
        .attr("y", 110)
        .attr("width", 50)
        .attr("height", 70)
        .style("fill", "gray");

    var gray3 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 230)
        .attr("y", 370)
        .attr("width", 30)
        .attr("height", 50)
        .style("fill", "gray");

    var gray4 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 370)
        .attr("y", 310)
        .attr("width", 40)
        .attr("height", 20)
        .style("fill", "gray");

    var gray5 = svg.append("rect").raise()
        .attr("class","block")
        .attr("x", 400)
        .attr("y", 20)
        .attr("width", 50)
        .attr("height", 30)
        .style("fill", "gray");


    // Updated draws
    d3.select("#update").on("click", function() {
        horizontalStreets([10,50,120,150,220,250,280,340,440])
        verticalStreets([10, 30, 70, 140, 300,330,420,440,480,500])
        d3.selectAll(".block").each(moveBlock)

    })

    yield svg.node();

}









